import React, {useState} from "react";
import {useQuery} from "@apollo/react-hooks";

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import useIntersect from "../intersection";

import {ALL_CHARACTERS} from "../queries";

import ErrorView from "../views/errorView";
import LoadingView from "../views/loadingView";
import CharactersView from "../views/charactersView";


export default function App() {
  let [after] = useState("");
  let [length] = useState(5);
  let character = useState(null);

  const {loading, error, data} = useQuery(ALL_CHARACTERS, {
    variables: {after: after, length: length},
  });

  if (loading) return <LoadingView/>;
  if (error) return <ErrorView/>;

  const [ref, entry] = useIntersect({
    threshold: 0.2
  });
  console.log(ref, entry);

  let characters = data.allPeople.edges;
  after = data.allPeople.pageInfo.endCursor;
  return (<CharactersView characters={characters} character={character} ref={ref} ratio={entry.intersectionRatio}/>);
}
