import React from "react";
import {useQuery} from '@apollo/react-hooks';
import {Card, CardHeader, CardBody, Navbar} from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";

//import PeopleItem from "../templates/peopleItem";

function App() {
  const getAllCharacters = useQuery(ALL_CHARACTERS, {variables: {length: 5}});
  const character = useQuery(FIND_CHARACTER_BY_ID, {variables: {id: "cGVvcGxlOjE="}});
  if (getAllCharacters.loading || character.loading) return (
    <div>
      <header>
        <Navbar className="navbar">
          <span className="navbar-brand"> Ravn Star Wars Registry</span>
        </Navbar>
      </header>
      <main className="container-fluid">
        <section>
          <div className="row">
            <div className="col-md-3">
              <p className="loading"><i/>Loading</p>
            </div>
          </div>
        </section>
      </main>
    </div>);
  if (getAllCharacters.error || character.error) return (<div>
    <header>
      <Navbar className="navbar">
        <span className="navbar-brand"> Ravn Star Wars Registry</span>
      </Navbar>
    </header>
    <main className="container-fluid">
      <section>
        <div className="row">
          <div className="col-md-3">
            <p className="error"><i/>;( Error</p>
          </div>
        </div>
      </section>
    </main>
  </div>);

  let listview = getAllCharacters.data.allPeople.edges.map((obj) => {
    let node = obj.node;
    return (<Card data-key={node.id}>
      <CardHeader><h2>{node.name}</h2></CardHeader>
      <CardBody>
        <p>{node.species.name} from {node.homeworld.name}</p>
      </CardBody>
    </Card>)
  });
  return (
    <main className="container-fluid">
      <nav className="navbar">
        <span className="navbar-brand"> Ravn Star Wars Registry</span>
      </nav>
      <section>
        {listview}
      </section>
    </main>
  )
}

export default App;
