import DetailCharacterView from "./detailCharacterView";
import React from "react";

let getCharacteristics = (props) => {
  let data = [];
  let keys = {
    eyeColor: 'Eye Color',
    hairColor: 'Hair Color',
    skinColor: 'Skin Color',
    birthYear: 'Birth Year'
  };
  let values = props.character;
  for (let i in values) {
    if (keys[i]) {
      data.push({'key': i, 'meta': keys[i], 'value': values[i]});
    }
  }
  return <DetailCharacterView data={data} title="General Information"/>;
};

let getVehicles = (props) => {
  let vehicles = props.character.vehicleConnection.edges.map(item => {
    return {key: item.node.id, meta: item.node.name};
  });
  return vehicles.length > 0 && (<DetailCharacterView data={vehicles} title="General Information"/>);
};

let DetailsCharacterView = (props) => {
  return (
    <div className="detail-character">
      {getCharacteristics(props)}
      {getVehicles(props)}
    </div>
  );
};

export default DetailsCharacterView;
