import React from "react";
import { useQuery } from '@apollo/react-hooks';
import {Card, CardHeader, CardBody, CardSubtitle} from 'reactstrap';

import "./../assets/scss/style.scss";

import { ALL_CHARACTERS, FIND_CHARACTER_BY_ID } from "../queries";
//import PeopleItem from "../templates/peopleItem";

function App() {
  const getAllCharacters = useQuery(ALL_CHARACTERS, {variables: {length: 5}});
  const character = useQuery(FIND_CHARACTER_BY_ID, { variables: { id: "cGVvcGxlOjE=" }});
  if (getAllCharacters.loading || character.loading) return (
    <main class="container">
      <nav></nav>
      <section>
        <p class="loading"><i/>Loading</p>
      </section>
    </main>);
  if (getAllCharacters.error || character.error) return <div>Error :(</div>;

  let listview = getAllCharacters.data.allPeople.edges.map((obj) =>{
    let node = obj.node;
    return (<Card data-key={node.id}>
      <CardHeader><h2>{node.name}</h2></CardHeader>
      <CardBody>
        <p>{node.species.name} from {node.homeworld.name}</p>
      </CardBody>
    </Card>)
  });
  return (

    <div className="container">
      {listview}
      <Card>
        <CardHeader>Query - Displaying data with args</CardHeader>
        <CardBody>
          <CardSubtitle>Viewing a user by id</CardSubtitle>
          <pre>
            {JSON.stringify(character.data, null, 2)}
          </pre>
        </CardBody>
      </Card>
    </div>
  )
}

export default App;
