import React from "react";
import {ListGroupItem} from "reactstrap";
import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";
import {useQuery} from "@apollo/react-hooks";

let handleClick = (id,callback) => {
  props.client.query({query: FIND_CHARACTER_BY_ID, variables: {id: props.node.id}});
  const { loading, error, data } = useQuery();
  callback(data.data.person);
};

let CharacterView = (props) => {
  return (
    <ListGroupItem onClick={handleClick.bind(props)}>
      <div>
        <h2>{props.node.name}</h2>
        <p>{props.node.species !== null ? props.node.species.name : ''} from {props.node.homeworld.name}</p>
      </div>
      <i className="arrow"/>
    </ListGroupItem>
  );
};

export default CharacterView;
