import {gql} from 'apollo-boost';

export const ALL_CHARACTERS = gql`
  query($length: Int)
  {
    allPeople(first:$length) {
      pageInfo{
          hasNextPage
      }
      edges {
        cursor    
        node {
          id
          name
          species{
            name
          }
          homeworld {
            name
          }
        }
      }
    }
  }
`;

export const FIND_CHARACTER_BY_ID = gql`
  query ($id: ID)
  {
    person(id: $id) {
      name
      eyeColor
      hairColor
      skinColor
      birthYear
      vehicleConnection{
          edges{
              node{
                  id,
                  name
              }   
          }
      }
    }
  }
`;
