import React from "react";
import {ListGroupItem} from "reactstrap";
import {useQuery} from "@apollo/react-hooks";
import {FIND_CHARACTER_BY_ID} from "../queries";

let getCharacterById = (id) => {
  console.log(useQuery(FIND_CHARACTER_BY_ID, {variables: {id: id}}));
};

const CharacterItemComponent = () => {
  return <ListGroupItem onClick={getCharacterById(this.props.node.id)}>
    <div>
      <h2>{this.props.node.name}</h2>
      <p>{this.props.node.species.name} from {this.props.node.homeworld.name}</p>
    </div>
    <i className="arrow"/>
  </ListGroupItem>;
};

export default CharacterItemComponent;
