import React from "react";
import {useQuery} from '@apollo/react-hooks';
import {Navbar} from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";
import CharacterItemComponent from "../templates/characterItemComponent";
import ErrorComponent from "../templates/errorComponent";
import LoadingComponent from "../templates/LoadingComponent";

//import PeopleItem from "../templates/peopleItem";

function App() {
  const getAllCharacters = useQuery(ALL_CHARACTERS, {variables: {length: 5}});
  const character = useQuery(FIND_CHARACTER_BY_ID, {variables: {id: "cGVvcGxlOjE="}});
  let content;
  if (getAllCharacters.loading || character.loading){
    content = <LoadingComponent/>;
  }
  if (getAllCharacters.error || character.error){
    content = <ErrorComponent/>;
  }

  if(!getAllCharacters.loading && !getAllCharacters.error){
    let listview = getAllCharacters.data.allPeople.edges.map((obj) => {
      return <CharacterItemComponent node={obj.node}/>
    });
    content = (<section>
      <div className="row">
        <div className="col-md-3">
          {listview}
        </div>
      </div>
    </section>);
  }

  return (
    <div>
      <header>
        <Navbar className="navbar">
          <span className="navbar-brand"> Ravn Star Wars Registry</span>
        </Navbar>
      </header>
      <main className="container-fluid">
        {content}
      </main>
    </div>
  )
}

export default App;
