import LayoutView from "./layoutView";
import CharacterView from "./characterView";
import DetailCharacterView from "./detailsCharacterView";

let CharactersView = (props) => {
  let [setCharacter] = useState(0);
  setCharacter = props.character;
  return (
    <LayoutView>
      <section>
        <div className="row">
          <div className="col-md-3">
            <ul className="characters">
              {props.characters.map(obj => {
                return <CharacterView key={obj.node.id} node={obj.node} onClick={() => setCharacter}/>
              })}
