import React from "react";
import {render} from "react-dom";
import InfiniteScroll from "react-infinite-scroll-component";

//import {ApolloProvider, Query} from "react-apollo";
//import mobiscroll from "@mobiscroll/react-lite";
//import '@mobiscroll/react-lite/dist/css/mobiscroll.min.css';

//import api from "./hocs/api";

/*import VehicleItem from "./templates/vehicleItem";
import PeopleItem from "./templates/peopleItem";
import CharacteristicItem from "./templates/characteristicItem";*/

const style = {
  height: 30,
  border: "1px solid green",
  margin: 6,
  padding: 8
};

class App extends React.Component {
  state = {
    items: Array.from({length: 5})
  };

  fetchMoreData = () => {
    // a fake async api call like which sends
    // 20 more records in 1.5 secs
    setTimeout(() => {
      this.setState({
        items: this.state.items.concat(Array.from({length: 20}))
      });
    }, 1500);
  };

  render() {
    return (
      <div>
        <h1>demo: react-infinite-scroll-component</h1>
        <hr/>
        <InfiniteScroll
          dataLength={this.state.items.length}
          next={this.fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
          scrollableTarget="scrollableDiv"
          // below props only if you need pull down functionality
          refreshFunction={this.fetchMoreData}
          pullDownToRefresh
          pullDownToRefreshContent={
            <h3 style={{textAlign: 'center'}}>&#8595; Pull down to refresh</h3>
          }
          releaseToRefreshContent={
            <h3 style={{textAlign: 'center'}}>&#8593; Release to refresh</h3>
          }
        >
          {this.state.items.map((i, index) => (
            <div style={style} key={index}>
              div - #{index}
            </div>
          ))}
        </InfiniteScroll>
      </div>
    );
  }
}


