import ApolloClient, {gql} from "apollo-boost";
import React from "react";

const client = new ApolloClient({
  uri: "https://swapi-graphql-ravn.herokuapp.com/"
});

export default {
  async listCharacters() {
    let data = await client.query({
      query: gql`
      {
          allPeople(first:5) {
            pageInfo{
                hasNextPage
            }
            edges {
              cursor    
              node {
                id
                name
                species{
                  name
                }
                homeworld {
                  name
                }
              }
            }
          }
        }
    `
    }).then((loading, error, data) => {
      if (loading) return {
        status: false,
        message: "Loading",
        data: []
      };
      if (error) return {
        status: false,
        message: error,
        data: []
      };
      return {
        status: true,
        message: 'Success',
        data: data.allPeople.edges.map(item => item.node)
      };
    });
  }
};
