import React from "react";
import {withApollo} from "@apollo/react-hoc";
import {Query} from "react-apollo";
import {Navbar} from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS} from "../queries";
import ErrorComponent from "./errorComponent";
import LoadingComponent from "./LoadingComponent";
import CharacterItemComponent from "./characterItemComponent";

class AppComponent extends React.Component {

  getContent(data) {
      let listView = getAllCharacters.data.allPeople.edges.map((obj) => {
        return <CharacterItemComponent node={obj.node} client={this.props.client} key={obj.node.id}/>
      });
      content = (<section>
        <div className="row">
          <div className="col-md-3">
            {listView}
          </div>
        </div>
      </section>);
  }

  render() {
    return (<Query
      query={ALL_CHARACTERS}>
      {({ data, loading, error, refetch, networkStatus }) => {
        if (loading) return <LoadingComponent/>;
        if (error) return <ErrorComponent/>;
        return (
          <div>
            <header>
              <Navbar className="navbar">
                <span className="navbar-brand"> Ravn Star Wars Registry</span>
              </Navbar>
            </header>
            <main className="container-fluid">
              {this.state.content}
            </main>
          </div>
        )
      }}
    </Query>);
  }
}

export default withApollo(AppComponent);
