import React from "react";
import {useQuery} from '@apollo/react-hooks';
import {Navbar} from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";
import CharacterItemComponent from "./characterItemComponent";
import ErrorComponent from "./errorComponent";
import LoadingComponent from "./LoadingComponent";

class AppComponent extends React.Component {

  constructor(props) {
    super(props);

    this.getAllCharacters = useQuery(ALL_CHARACTERS, {variables: {length: 5}});
    this.character = useQuery(FIND_CHARACTER_BY_ID, {variables: {id: "cGVvcGxlOjE="}});

    if (this.getAllCharacters.loading || this.character.loading){
      this.content = <LoadingComponent/>;
    }
    if (this.getAllCharacters.error || this.character.error){
      this.content = <ErrorComponent/>;
    }
    if(!this.getAllCharacters.loading && !this.getAllCharacters.error){
      let listView = this.getAllCharacters.data.allPeople.edges.map((obj) => {
        return <CharacterItemComponent node={obj.node}/>
      });
      this.content = (<section>
        <div className="row">
          <div className="col-md-3">
            {listView}
          </div>
        </div>
      </section>);
    }
  }
  render() {
    return (
      <div>
        <header>
          <Navbar className="navbar">
            <span className="navbar-brand"> Ravn Star Wars Registry</span>
          </Navbar>
        </header>
        <main className="container-fluid">
          {this.content}
        </main>
      </div>
    )
  }
}

export default AppComponent;
