import React from "react";

class DetailComponent extends React.Component {
  getCharacteristics(){
    return (<div className="general-information">
      <h3>General Information</h3>
      <ul>
        <li className="quality">
          <span className="title">Eye Color</span>
          <span className="data">{this.props.character.eyeColor}</span>
        </li>
        <li className="quality">
          <span className="title">Hair Color</span>
          <span className="data">{this.props.character.hairColor}</span>
        </li>
        <li className="quality">
          <span className="title">Skin Color</span>
          <span className="data">{this.props.character.skinColor}</span>
        </li>
        <li className="quality">
          <span className="title">Birth Year</span>
          <span className="data">{this.props.character.birthYear}</span>
        </li>
      </ul>
    </div>);
  }

  getVehicles() {
    let vehicles = this.props.character.vehicleConnection.edges;
    return  vehicles.length >0 && (
      <div className="vehicles"><h3>Vehicles</h3><ul>
        {vehicles.map(item => {
          return (<li className="quality" key={item.node.id}>
            <span className="title">{item.node.name}</span>
          </li>);
        })}
      </ul></div>);
  }

  render() {
    return (<div className="detail-character">
      {this.getCharacteristics()}
      {this.getVehicles()}
    </div>);
  }
}

export default DetailComponent;
