import React, { useState } from "react";
import {useQuery} from "@apollo/react-hooks";

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS} from "../queries";

import ErrorView from "../views/errorView";
import LoadingView from "../views/loadingView";
import CharactersView from "../views/charactersView";


export default function App() {
  let [characters, character, hasNext, after, length] = [
    useState([]),
    useState(null),
    useState(false),
    useState(""),
    useState(5)
  ];
  const { loading, error, data } = useQuery(ALL_CHARACTERS, {variables:{after:after,length:length}});

  if(loading) return <LoadingView/>;
  if(error) return <ErrorView/>;

  characters = data.allPeople.edges;
  after = data.allPeople.pageInfo.endCursor;
  return (<CharactersView characters={characters} character={character}/>);
}
