import React from "react";
import {useQuery} from '@apollo/react-hooks';
import {Navbar, ListGroupItem} from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";

//import PeopleItem from "../templates/peopleItem";

function App() {
  const getAllCharacters = useQuery(ALL_CHARACTERS, {variables: {length: 5}});
  const character = useQuery(FIND_CHARACTER_BY_ID, {variables: {id: "cGVvcGxlOjE="}});
  let content;
  if (getAllCharacters.loading || character.loading){
    content = (
      <section>
        <div className="row">
          <div className="col-md-3">
            <div className="loading">
              <div className="ml-loader">
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
                <div></div>
              </div>
              Loading
            </div>
          </div>
        </div>
      </section>);
  }
  if (getAllCharacters.error || character.error){
    content = (
      <section>
        <div className="row">
          <div className="col-md-3">
            <div className="error">Failed to Load Data</div>
          </div>
        </div>
      </section>);
  }

  if(!getAllCharacters.loading && !getAllCharacters.error){
    let listview = getAllCharacters.data.allPeople.edges.map((obj) => {
      let node = obj.node;
      return (<ListGroupItem key={node.id}>
        <h2>{node.name}</h2>
        <p>{node.species.name} from {node.homeworld.name}</p>
      </ListGroupItem>)
    });
    content = (<section>
      <div className="row">
        <div className="col-md-3">
          {listview}
        </div>
      </div>
    </section>);
  }

  return (
    <div>
      <header>
        <Navbar className="navbar">
          <span className="navbar-brand"> Ravn Star Wars Registry</span>
        </Navbar>
      </header>
      <main className="container-fluid">
        {content}
      </main>
    </div>
  )
}

export default App;
