import React, { useState } from "react";
import {useQuery} from "@apollo/react-hooks";

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";

import ErrorView from "../views/errorView";
import LoadingView from "../views/loadingView";
import CharactersView from "../views/charactersView";


export default function App() {
  const [characters, character, hasNext, after] = useState(0);
  const { loading, error, data } = useQuery(ALL_CHARACTERS);

  if(loading) return <LoadingView/>;
  if(error) return <ErrorView/>;

  [characters] = data.allPeople.edges;
  return (<CharactersView characters={characters} character={character}/>);
}
