import React, {useState} from "react";
import LayoutView from "./layoutView";
import CharacterView from "./characterView";
import DetailsCharacterView from "./detailsCharacterView";
import {ALL_CHARACTERS} from "../queries";
import {withApollo} from "@apollo/react-hoc";

let loadMore = async (props) => {
  let data = await props.client.query({query: ALL_CHARACTERS, variables: {after: props.after, length: props.length}});
  console.log([...props.characters, ...data.data.allPeople.edges]);
  props.setCharacters([...props.characters, ...data.data.allPeople.edges]);
  props.setHasNext(data.data.allPeople.pageInfo.hasNextPage);
  props.setAfter(data.data.allPeople.pageInfo.endCursor);
};


let CharactersView = async (props) => {
  let [character, setCharacter] = useState(null);
  if (props.hasNext) await loadMore(props);

  let characterView = props.characters.map(obj => {
    return <CharacterView key={obj.node.id} node={obj.node} setCharacter={setCharacter}/>
  });

  return (
    <LayoutView>
      <section>
        <div className="row">
          <div className="col-md-3">
            <ul className="characters">
              {characterView}
            </ul>
          </div>
          <div className="col-md-9">
            {character !== null ? <DetailsCharacterView character={character}/> : ''}
          </div>
        </div>
      </section>
    </LayoutView>
  );
};

export default withApollo(CharactersView);
