import React from "react";
import { useQuery } from '@apollo/react-hooks';


import { ALL_CHARACTERS, FIND_CHARACTER_BY_ID } from "../queries";
import PeopleItem from "../templates/peopleItem";

function App() {
  const getAllCharacters = useQuery(ALL_CHARACTERS, {variables: {length: 5}});
  const character = useQuery(FIND_CHARACTER_BY_ID, { variables: { id: "cGVvcGxlOjE=" }});
  if (getAllCharacters.loading || character.loading) return <div>Loading</div>;
  if (getAllCharacters.error || character.error) return <div>Error :(</div>;

  return (
    <mobiscroll.Form theme="mobiscroll" className="md-master-detail-demo">
      <div className="mbsc-grid mbsc-no-padding">
        <div className="mbsc-row">
          <div className="mbsc-col-12 mbsc-col-md-2 mbsc-col-sm-3">
            <mobiscroll.Listview select="single" itemType={PeopleItem} data={getAllCharacters} />
          </div>
          <div className="mbsc-col-12 mbsc-col-md-10 mbsc-col-sm-9 md-col-border">
            {content}
          </div>
        </div>
      </div>
    </mobiscroll.Form>
  );
