import React from "react";
import {render} from "react-dom";
import { useQuery } from '@apollo/react-hooks';
import { Card, CardBody, CardHeader, CardSubtitle, Spinner } from 'reactstrap';

import { ALL_CHARACTERS, FIND_CHARACTER_BY_ID } from "./Queries";

//import mobiscroll from "@mobiscroll/react-lite";
//import '@mobiscroll/react-lite/dist/css/mobiscroll.min.css';

//import api from "./hocs/characters";

//import VehicleItem from "./templates/vehicleItem";
//import PeopleItem from "./templates/peopleItem";
//import CharacteristicItem from "./templates/characteristicItem";


class App extends React.Component {
  constructor(props) {
    super(props);

    this.getAllCharacters = useQuery(ALL_CHARACTERS);
    this.character = useQuery(FIND_CHARACTER_BY_ID, { variables: { id: 'cGVvcGxlOjE=' }});
    if (this.getAllCharacters.loading || this.character.loading) return <Spinner color="dark" />;
    if (this.getAllCharacters.error || this.character.error) return <React.Fragment>Error :(</React.Fragment>;

    /*this.state = {
      characters: api.listCharacters(),
      selectedCharacter: null,
      data: []
    };
    this.getData();*/
  }

  render() {
    /*let content = '';
    const selectedPerson = this.state.selectedPerson;

    if(selectedPerson !== null){
      content = (<div className="mbsc-form-grid">
        <mobiscroll.CardHeader>
          <span className="mbsc-bold">General information</span>
        </mobiscroll.CardHeader>
        <mobiscroll.CardContent>
          <mobiscroll.Listview itemType={CharacteristicItem} data={this.state.characteristics}/>
        </mobiscroll.CardContent>
        <mobiscroll.CardHeader>
          <span className="mbsc-bold">Vehicles</span>
        </mobiscroll.CardHeader>
        <mobiscroll.CardContent>
          <mobiscroll.Listview itemType={VehicleItem} data={this.state.vehicles}/>
        </mobiscroll.CardContent>
      </div>);
    }

    return (
      <mobiscroll.Form theme="mobiscroll" className="md-master-detail-demo">
        <div className="mbsc-grid mbsc-no-padding">
          <div className="mbsc-row">
            <div className="mbsc-col-12 mbsc-col-md-2 mbsc-col-sm-3">
              <mobiscroll.Listview select="single" itemType={PeopleItem} data={this.state.people} onItemTap={this.changePerson} />
            </div>
            <div className="mbsc-col-12 mbsc-col-md-10 mbsc-col-sm-9 md-col-border">
              {content}
            </div>
          </div>
        </div>
      </mobiscroll.Form>
    );*/
    return (
      <div className="container">
        <Card>
          <CardHeader>Query - Displaying all data</CardHeader>
          <CardBody>
          <pre>
            {JSON.stringify(this.getAllCharacters.data, null, 2)}
          </pre>
          </CardBody>
        </Card>
        <Card>
          <CardHeader>Query - Displaying data with args</CardHeader>
          <CardBody>
            <CardSubtitle>Viewing a user by id</CardSubtitle>
            <pre>
            {JSON.stringify(this.character.data, null, 2)}
          </pre>
          </CardBody>
        </Card>
      </div>
    )
  }
