import React from "react";
import {withApollo} from "@apollo/react-hoc";
import {Query} from 'react-apollo';

import 'bootstrap/dist/css/bootstrap.min.css';
import "./../assets/scss/style.scss";

import {ALL_CHARACTERS, FIND_CHARACTER_BY_ID} from "../queries";
import ErrorComponent from "./errorComponent";
import LoadingComponent from "./loadingComponent";
import CharacterItemComponent from "./characterItemComponent";
import DetailsComponent from "./detailsComponent";

class AppComponent extends React.Component {

  constructor(props) {
    super(props);
    this.length = 5;
    this.state = {character: null, characters: []};
  }

  getVariables() {
    return {length: this.length};
  }

  async getCharacterById(id, client){
    let character = (await client.query({
      query: FIND_CHARACTER_BY_ID,
      variables: {id: id},
    })).data.person;
    this.setState({character: character});
  };

  renderData(data){
    let listView = data.allPeople.edges.map((obj) => {
      return <CharacterItemComponent node={obj.node} key={obj.node.id}  getCharacterById={this.getCharacterById.bind(this)} />
    });
    return (<section>
      <div className="row">
        <div className="col-md-3">
          <ul className="characters">
            {listView}
          </ul>
        </div>
        <div className="col-md-9">
          {this.state.character !== null?<DetailsComponent character={this.state.character}/>:''}
        </div>
      </div>
    </section>);
  }

  executeQuery({loading, error, data}) {
    if (loading) return <LoadingComponent/>;
    if (error) return <ErrorComponent/>;
    this.state.characters
    this.setState({characters: [...this.state.characters, data]});
    console.log(this.state.character);
    return this.renderData(data);
  }

  render() {
    return (
      <div>
        <header>
          <nav className="navbar">
            <span className="navbar-brand"> Ravn Star Wars Registry</span>
          </nav>
        </header>
        <main className="container-fluid">
          <Query query={ALL_CHARACTERS} variables={this.getVariables()}>
            {this.executeQuery.bind(this)}
          </Query>
        </main>
      </div>
    )
  }
}

export default withApollo(AppComponent);
