import React from "react";
import {withApollo} from "@apollo/react-hoc";
import {ListGroupItem} from "reactstrap";
import {FIND_CHARACTER_BY_ID} from "../queries";

class CharacterItemComponent extends React.Component {

  getCharacterById = () => {
    let id = this.props.node.id;
    this.props.client.query({
      query: FIND_CHARACTER_BY_ID,
      variables: {id: id},
    });
  };

  render() {
    return (<ListGroupItem onClick={this.getCharacterById}>
      <div>
        <h2>{this.props.node.name}</h2>
        <p>{this.props.node.species.name} from {this.props.node.homeworld.name}</p>
      </div>
      <i className="arrow"/>
    </ListGroupItem>);
  }
}

export default withApollo(CharacterItemComponent);
