import React from "react";
import DetailComponent from "./detailComponent";

class DetailsComponent extends React.Component {
  getCharacteristics() {
    let data = [];
    let keys = {
      eyeColor: 'Eye Color',
      hairColor: 'Hair Color',
      skinColor: 'Skin Color',
      birthYear: 'Birth Year'
    };
    let values = this.props.character;
    for(let i in values){
      if(keys[i]){
        data.push({'key': i, 'meta': keys[i], 'value': values[i]});
      }
    }
    console.log(data);
    return <DetailComponent data={data} title="General Information"/>;
  }

  getVehicles() {
    let vehicles = this.props.character.vehicleConnection.edges.map(item => {
      return {key: item.node.id, meta:item.node.name};
    });
    return vehicles.length > 0 && (<DetailComponent data={vehicles} title="General Information"/>);
  }

  render() {
    return (<div className="detail-character">
      {this.getCharacteristics()}
      {this.getVehicles()}
    </div>);
  }
}

export default DetailsComponent;
