import React, { useState } from "react";
import LayoutView from "./layoutView";
import CharacterView from "./characterView";
import DetailCharacterView from "./detailsCharacterView";

let CharactersView = (props) => {
  let [character] = useState(0);

  let setCharacter = (node) => {
      character = node;
  };

  return (
    <LayoutView>
      <section>
        <div className="row">
          <div className="col-md-3">
            <ul className="characters">
              {props.characters.map(obj => {
                return <CharacterView key={obj.node.id} node={obj.node} setCharacter={setCharacter}/>
              })}
            </ul>
          </div>
          <div className="col-md-9">
            {character !== null?<DetailCharacterView character={character}/>:''}
          </div>
        </div>
      </section>
    </LayoutView>
  );
};

export default CharactersView;
